import React from 'react'
import { Input, Image, Space, Button, message } from 'antd';
import { useState, useRef, useMemo } from 'react';
import { addBannerApi } from '@/api/banner';
import { useNavigate } from 'react-router-dom'
type Props = {}

export default function Addbanner({ }: Props) {
    const [link, setLink] = useState<string>('');
    const [alt, setAlt] = useState<string>('')
    const [img, setImg] = useState('')
    const ref1 = useRef<any>()
    const [messageApi, contextHolder] = message.useMessage();
    const navigate = useNavigate();

    const isdisabled = useMemo(() => {
        return link != '' && alt != '' && img != ''
    }, [link, alt, img])
    // 选择图片
    const changeImgFn = () => {
        // 获取当前文本内容
        console.log(ref1.current.input.files[0]);
        const file = ref1.current.input.files[0]
        // 创建一个文件的读取器(将文件流读取成某一种需要的格式)
        const reader = new FileReader();
        // 使用读取器将文件读取成base64格式(读取时异步读取,不是一下就读完了)
        reader.readAsDataURL(file)
        // 监听读取完成的结果 (绑定一个事件去监听)
        reader.onload = function () {
            // console.log(this.result);
            setImg(this.result as string)
        }
    }

    return (
        <Space direction='vertical' style={{ width: '500px' }}>
            {contextHolder}
            <div>
                <Input placeholder='link' value={link} onChange={(e) => {
                    setLink(e.target.value);
                }}></Input>
            </div>
            <div>
                <Input placeholder='alt' value={alt} onChange={(e) => {
                    setAlt(e.target.value);
                }}></Input>
            </div>
            <div>
                <Image
                    width={200}
                    height={200}
                    src={img}
                    fallback='data:image/png;base64,'
                >
                </Image>
            </div>
            <div>
                <Input type='file' ref={ref1} onChange={changeImgFn}></Input>
            </div>
            <div>
                {/*  显示base64图片格式的 */}
                <Input placeholder='base64图片格式' type='text' value={img}></Input>
            </div>

            <div>
                <Button type='primary' disabled={!isdisabled} onClick={() => {
                    // 调用接口
                    addBannerApi({
                        img,
                        alt,
                        link
                    }).then((res: any) => {
                        // console.log(res);
                        messageApi.open({
                            type: res.code == 200 ? 'success' : 'error',
                            content: res.message,
                            duration: 1,
                            onClose() {
                                //  跳转到轮播图列表页
                                if (res.code == 200) {
                                    navigate('/banner/bannerlist')
                                    return
                                }
                            },
                        })

                    })
                }}>提交</Button>
            </div>
        </Space >
    )
}